		internal static Texture2D CreateOrLoad(IconType iconType)
		{
			var base64 = _encodedIconsBase64[iconType];

			var texture2DName = $"{base64.GetHashCode()}";

			return (LoadTexture2D(texture2DName) ?? CreateTexture2D(base64, texture2DName));
		}

		private static Texture2D CreateTexture2D(String base64, String name)
		{
			try
			{
				var bytes = Convert.FromBase64String(base64);
				var texture2D = new Texture2D(1, 1, TextureFormat.ARGB32, false, false);

				texture2D.name = name;
				texture2D.hideFlags = HideFlags.HideAndDontSave;
				texture2D.LoadImage(bytes);

				return texture2D;
			}
			catch (Exception exception)
			{
				Debug.LogError($"Failed to load texture \"{name}\": {exception}");
				return null;
			}
		}

		private static Texture2D LoadTexture2D(String texture2DName)
		{
			try
			{
				return Resources.FindObjectsOfTypeAll<Texture2D>().FirstOrDefault(texture2D => (texture2D.name == texture2DName));
			}
			catch (Exception exception)
			{
				Debug.LogError($"[{typeof(EncodedIcons)}] Failed to load {nameof(Texture2D)} \"{texture2DName}\": {exception}");
				return null;
			}
		}
	}
}